[CmdletBinding()]
Param(
    [parameter(Mandatory = $true, ValueFromPipeline = $true)][string]$line
)

######################################################
### Ed-Fi ODS v3+
### Assessment: Illinois Assessment of Readiness (IRA)
### Script: Custom File Processor
######################################################

begin {
    # Set up the statistics
    # validations: Hashtable of HashTables with properties for each screening period, and each screening period will have statistics like number of records not loaded, and detailed info
    <#
    #>
    $global:statistics = @{
        "rowsProcessed" = 0
        "rowsLoaded"    = 0
        "validations"   = @{}
    }

    # Prepare some other variables
    $currentLineNumber = 0
    $global:inputHeaders = @()
    $global:headersLookup = @{}

    ## ODS functions
    ##set ApiVersion 
    $global:apiVersion = $DataImport.ApiVersion
    
    function Get-StudentUniqueIdType ($apiVersion) {
        <#
            .SYNOPSIS
                Determines whether the value in Student.studentUniqueId is a District or State ID value. 
    
            .DESCRIPTION
                Determining the studentUniqueId value type is done by querying the /students endpoint for 10 
                students and then checking either the values in the identificationCodes[] array on either the
                Student record (v2.5+ API) or the studentEducationOrganizationAssociation record (v3.x API) to
                see whether the District or State ID matches the StudentUniqueId value. 10 students are checked 
                to make sure there aren't any discrepancies.
    
                NOTE: Only "District" and "State" Student ID types are used in this check; all others are ignored!
    
            .PARAMETER ApiVersion
                The version number of the API as set in $DataImport.ApiVersion. For whatever reason the $DataImport
                object is not directly addressable from within a module.
    
            .OUTPUTS
                A string representing the type of ID stored in studentUniqueId. This value  will be either
                'District' or 'State'.
    
                Throws an error on any failure to connect to or query the API or if one or more students of
                the 10 checked have a different ID value type (e.g. one has 'District' while the rest have 
                'State'). Also throws an error if a match can't be made on any of the 10 students checked.
    
            .EXAMPLE
                $studentUniqueIdType = Get-StudentUniqueIdType -apiVersion $DataImport.ApiVersion
        #>
    
        # Do sanity checks on parameters
        if (!$apiVersion) { throw "The apiVersion parameter must be set to a valid value." }
    
        # Processing depends on API version
        # Only valid for apis version 3 and above
        
        if ($apiVersion.StartsWith("3.") -or $apiVersion.StartsWith("5.") -or $apiVersion.StartsWith("6.")) {
            # Query for 10 students (whatever comes back first - and yes, this could be flaky)
            try {
                $jsonResponse = Invoke-OdsApiRequest -RequestPath "/ed-fi/studentEducationOrganizationAssociations?limit=10"
                $seoas = ConvertFrom-Json $jsonResponse
            }
            catch {
                throw $_
            }
            if ($seoas.Count -eq 0) { throw "Unable to determine StudentUniqueId type as no StudentEducationOrganizationAssociations were found in the ODS." }
        
            # Iterate the list of students checking studentUniqueId against the ID code types. These should all be the same but you know student data...
            $ret = ""
            foreach ($seoa in $seoas) {
                foreach ($idCode in $seoa.studentIdentificationCodes) {
                    if ($seoa.studentReference.studentUniqueId -eq $idCode.identificationCode) {
                        $codeType = $idCode.studentIdentificationSystemDescriptor.Split("#")
                        $codeType = $codeType[1]
                        if ($codeType -in @("District", "State")) {
                            if ($ret -eq "") { $ret = $codeType }
                            elseif ($codeType -ne $ret) { 
                                throw "Unable to determine studentUniqueId type as StudentEducationOrganizationAssociations have been found with both District and State ID values in studentUniqueId" 
                            }
                        }
                    }
                }
            }
        
            # If we got here then all 10 students had the same type of studentUniqueId value, so return that type
            return $ret
        }
        else {
            throw "Attempt to connect to a version $($apiVersion) API, which is unsupported."
        }
        
    }

    function Get-StudentsIdMap ($apiVersion, $studentIdTypeCode) {
        <#
        .SYNOPSIS
            Load from ODS studentId mapping from a given studentId type to its corresponding UniqueStudentId. $studentIdTypeCode valid values 'District','State'

        .DESCRIPTION
            Loads StudentUniqueId mapped to a passed studentId type from the ODS. 
            This data can then be used to look up a StudentUniqueId value by a specific studentIdType.

        .PARAMETER ApiVersion
            The version number of the API as set in $DataImport.ApiVersion. For whatever reason the $DataImport
            object is not directly addressable from within a module.

        .PARAMETER StudentIdTypeCode
            The studentId Type that needs to be mapped to StudentUniqueId.

        .OUTPUTS
            A complex HashMap of values that you can treat as an object. The format is:
            - StudentIdMap: Hashmap where keys are the desired studentIdType IdentificationCode value and the value is the StudentUniqueId from ODS
                - Student Records (Key: StudentIdType IdentificationCode from ODS; Value: StudentUniqueId
            
            Throws an error on any failure to connect to or query the API or if one or more students of
            the 10 checked have a different ID value type (e.g. one has 'District' while the rest have 
            'State')

        .EXAMPLE
            $students = Get-StudentsIdMap -apiVersion $DataImport.ApiVersion -studentIdTypeCode "State"
    #>

        # Set up some basic varialbes
        $ret = @{
            "StudentIdMap" = @{}
            # "DuplicateStudents"         = @()
            # "NoAdditionalIdValuesFound" = 0
        }
        $continue = $true
        $offset = 0
        $limit = 100

        # Do sanity checks on parameters
        if (!$apiVersion) { throw "The apiVersion parameter must be set to a valid value." }

        # Processing depends on the API version
        if ($apiVersion.StartsWith("3.") -or $apiVersion.StartsWith("5.") -or $apiVersion.StartsWith("6.")) {
            try {
                # Load the additional ID values
                $continue = $true
                $offset = 0
                while ($continue) {
                    $response = Invoke-OdsApiRequest -RequestPath "/ed-fi/studentEducationOrganizationAssociations?limit=$limit&offset=$offset"
                    $students = ConvertFrom-Json $response
                    if ($students.Count -gt 0) {
                        foreach ($student in $students) {
                            $studentUniqueId = $student.studentReference.studentUniqueId
                            foreach ($idCode in $student.studentIdentificationCodes) {
                                
                                if ($idCode.studentIdentificationSystemDescriptor.Contains($studentIdTypeCode)) { 
                                    $ret.StudentIdMap[$idCode.identificationCode] = $studentUniqueId 
                                    # If found stop loop
                                    break
                                }
                            }
                        }
                    }
                    else {
                        $continue = $false
                    }
                    $offset += $limit
                }
        
                # Return the data
                return $ret
            }
            catch {
                throw $_
            }
        }
        else {
            throw "Attempt to connect to a version $($apiVersion) API, which is unsupported."
        }

    }
    #Create list Student Id  lookup
    function Get-StudentIdLookupNeeded ($studentIdType){
         Write-Information "$studentIdType"
        # Get the studentUniqueIdType
        Write-Information "Checking StudentUniqueIdType..."
        $global:studentUniqueIdType = Get-AgentCacheItem -Key "StudentUniqueIdType"
        
        if (!$global:studentUniqueIdType) {
            try {
                $global:studentUniqueIdType = Get-StudentUniqueIdType -apiVersion $apiVersion
                New-AgentCacheItem -Key "StudentUniqueIdType" -Value $global:studentUniqueIdType
                if ($global:studentUniqueIdType) {
                    Write-Information "StudentUniqueId Type: $($global:studentUniqueIdType)"
                }  
            }
            catch {
                Write-Error "Failed to determine studentUniqueId type. Error returned is '$($_.Exception.Message)'"
                throw "Fatal error while running Begin section of Preprocessor."
            }
        }

        $global:needStudentIdLookup = $false
        # Only if we are able to find studentIdType in ODS
        if ($studentUniqueIdType) {
            # Check whether student id in ODS matches the studentId in the assessment file.
            if ($studentIdType -ne $studentUniqueIdType) {
                $global:needStudentIdLookup = $true

                # Only load students if IDs do not match(file StudentId vs ODS UniqueStudentId)
                # Load the list of students
                Write-Information "Loading list of studentId mappings ..."
                $STUDENT_ID_MAPPING_CACHE_KEY = "StudentsIdMap"
                $global:studentsIdMap = Get-AgentCacheItem -Key $STUDENT_ID_MAPPING_CACHE_KEY
                if (!$global:studentsIdMap) {
                    try {
                        $s = Get-StudentsIdMap -apiVersion $apiVersion -studentIdTypeCode $studentIdType
                        Write-Information "Loaded $($s.StudentIdMap.Count) StudentId mapping records from ODS."
                        $global:studentsIdMap = $s.StudentIdMap
                        New-AgentCacheItem -Key $STUDENT_ID_MAPPING_CACHE_KEY -Value $global:studentsIdMap
                    }
                    catch {
                        Write-Error "Failed to load list of students from the ODS> Error returned is '$($_.Exception.Message)'"
                        throw "Fatal error while running Begin section of Preprocessor."
                    }
                    if ($global:studentsIdMap.Count -eq 0) {
                        throw "StudentId from file does not match ODS student id and we were not able to load id mapping from ODS(relation from ODS UniqueId to studentId in file)."
                    }
                }
            }
        }
        else {
            Write-Warning "Unable to find StudentId Type. Script will proceed and use student identifier as it comes in assessment file."
        }
        return $needStudentIdLookup
    }

    ## Helper functions  

    # Get a parsed record in Hashtable where properties are the headers(used for CSV data only)
    function Get-ParsedRecord($headers, $line) {
        # Deal with double-quote wrappers and the fact that fields may have embedded commas
        # NOTE: This is vastly stupid, but we can't run Regex in ConstrainedLanguage mode so it is what it is.
        if ($line.Contains('","') -or $line.Contains(',"') -or $line.Contains('",')) {
            $cleanLine = $line
            $inQuote = $false
            for ($i = 0; $i -le $cleanLine.length; $i++) {
                if ($cleanLine[$i] -eq '"') {
                    $inQuote = !$inQuote
                }
                else {
                    if ($cleanLine[$i] -eq "," -and $inQuote -eq $false) {
                        $cleanLine = $cleanLine.Substring(0, $i) + "^" + $cleanLine.Substring($i + 1)
                    }
                }
            }
            $cleanLine = $cleanLine.Replace(",", "")
            $fields = $cleanLine.split("^")
        }
        else {
            $fields = $line.split(",")
        }
        $ret = @{}
        for ($i = 0; $i -lt $fields.Length; $i++) {
            $ret.Add($headers[$i], ($fields[$i].Replace('"', ''))) | Out-Null
        }
        return $ret
    }

    # Get a parsed csv line in an array of values (used for CSV data only)
      function Get-ParsedRecordArray($line, $delimiter = ",") {
        # Deal with double-quote wrappers and the fact that fields may have embedded commas
        # NOTE: This is vastly stupid, but we can't run Regex in ConstrainedLanguage mode so it is what it is.
        if ($line.Contains('"{0}"' -f $delimiter ) -or $line.Contains('{0}"' -f $delimiter) -or $line.Contains('"{0}' -f $delimiter)) {
            $cleanLine = $line
            $inQuote = $false
            for ($i = 0; $i -le $cleanLine.length; $i++) {
                if ($cleanLine[$i] -eq '"') {
                    $inQuote = !$inQuote
                }
                else {
                    if ($cleanLine[$i] -eq $delimiter -and $inQuote -eq $false) {
                        $cleanLine = $cleanLine.Substring(0, $i) + "^" + $cleanLine.Substring($i + 1)
                    }
                }
            }
            $cleanLine = $cleanLine.Replace($delimiter, "")
            $fields = $cleanLine.split("^")
        }
        else {
            $fields = $line.split($delimiter)
        }
        $ret = @()
        for ($i = 0; $i -lt $fields.Length; $i++) {
            $ret += $fields[$i].Replace('"', '')
        }
        return $ret
    }

    # Parse Date from string
    function Get-ParsedDate ($date, $inputFormat, $outputFormat = $null) {
        <# 
                .SYNOPSIS
                    Parse a date string and returns it in the correct format.
        
                .PARAMETER date (required)
                    The date string to parse
        
                .PARAMETER inputFormat (required)
                    The expected format of the incoming date string
        
                .PARAMETER outputFormat (optional)
                    The desired format of the retured date string
        
                .OUTPUTS
                    If no outputFormat is provided then a Date object is returned
        
                    If outputFormat is provided then a formatted string is returned
        
                    If parsing fails for any reason then $null is returned
            #>
        
        try {
            $d = $([datetime]::ParseExact($date, $inputFormat, $null))
            if ($outputFormat) {
                $d = Get-Date -Date $d -Format $outputFormat
            }
            return $d
        }
        catch {
            Write-Warning "Error $_" 
            return $null
        }
    }

    # Add record to statistics indicating a record was not processed becuase of a validation error
    function Get-StudentODSId($fileStudentId) {
        if ($global:needStudentIdLookup) {
            return $global:studentsIdMap[$fileStudentId]
        }
        else {
            # StudentId matches ODS
            return $fileStudentId
        }
    }

    ## Helper functions        

    # Convert an array list of data into a CSV line for output
    function Get-CSVLine($inputData) {
        $ret = ""
        foreach ($cell in $inputData) {
            if ($ret -ne "") { $ret += "," }
            $ret += ('"' + $cell + '"')
        }
        return $ret
    }


    ### Assessment fields

    # Type of StudentId that will be used from assessment file to match student in ODS
    # Valid values 'District','State'
    $studentIdType = "District" 

    $global:fileColumnDelimiter = ","    
    
    #Create studentsIdMap 
    $needStudentIdLookup =  Get-StudentIdLookupNeeded($studentIdType)

    <#
        SchoolYear is to be extracted from filename.
        Filename format:
            "... expect to see "_SpringYYYY" in the name ..."
    #>
    $filename = $DataImport.Filename
    if ($filename) {
        $patternMatch = $filename -match "^.*_Spring(\d{4}).*$"
        if ($patternMatch) {
            ## SchoolYear
            # if pattern matches first match group is entire string, then second element( index 1) is the capturing group, in our case the 4 digits indicating schoolyear
            $global:SCHOOL_YEAR = $Matches[1]
        }
        else {
            $errorMessage = "[ERROR] Unable to extract SchoolYear from filename. Expected to find pattern _SpringYYYY in filename."
            Write-Error $errorMessage
            throw $errorMessage
        }
    }


    #$attr = $DataImport.MapAttribute

    # GradeLevelLookup
    $global:GRADE_LEVEL_LOOKUP = @{}
    # $global:GRADE_LEVEL_LOOKUP.Add("K", "uri://ed-fi.org/GradeLevelDescriptor#Kindergarten") | Out-Null
    $global:GRADE_LEVEL_LOOKUP.Add("1", "uri://ed-fi.org/GradeLevelDescriptor#First grade") | Out-Null
    $global:GRADE_LEVEL_LOOKUP.Add("2", "uri://ed-fi.org/GradeLevelDescriptor#Second grade") | Out-Null
    $global:GRADE_LEVEL_LOOKUP.Add("3", "uri://ed-fi.org/GradeLevelDescriptor#Third grade") | Out-Null
    $global:GRADE_LEVEL_LOOKUP.Add("4", "uri://ed-fi.org/GradeLevelDescriptor#Fourth grade") | Out-Null
    $global:GRADE_LEVEL_LOOKUP.Add("5", "uri://ed-fi.org/GradeLevelDescriptor#Fifth grade") | Out-Null
    $global:GRADE_LEVEL_LOOKUP.Add("6", "uri://ed-fi.org/GradeLevelDescriptor#Sixth grade") | Out-Null	
    $global:GRADE_LEVEL_LOOKUP.Add("7", "uri://ed-fi.org/GradeLevelDescriptor#Seventh grade") | Out-Null
    $global:GRADE_LEVEL_LOOKUP.Add("8", "uri://ed-fi.org/GradeLevelDescriptor#Eighth grade") | Out-Null
    $global:GRADE_LEVEL_LOOKUP.Add("9", "uri://ed-fi.org/GradeLevelDescriptor#Ninth grade") | Out-Null
    $global:GRADE_LEVEL_LOOKUP.Add("10", "uri://ed-fi.org/GradeLevelDescriptor#Tenth grade") | Out-Null
    $global:GRADE_LEVEL_LOOKUP.Add("11", "uri://ed-fi.org/GradeLevelDescriptor#Eleventh grade") | Out-Null
    $global:GRADE_LEVEL_LOOKUP.Add("12", "uri://ed-fi.org/GradeLevelDescriptor#Twelfth grade") | Out-Null


    ######################
    ### Columns Definition 
    ######################
   
    # Custom/computed output columns. key: output column name, value: HashTable with source column name and other info
    ##AssessmentIdentifier --> Test Name
    
    $global:CUSTOM_COLUMN_MAP = @{}
    $global:CUSTOM_COLUMN_MAP.Add("DI_StudentId", 
        @{ "SourceColumnName" = "Local Student Identifier"; }) | Out-Null
    
    $global:CUSTOM_COLUMN_MAP.Add("DI_AdministrationDate", 
        @{ "SourceColumnName" = ""; }) | Out-Null
    
    $global:CUSTOM_COLUMN_MAP.Add("DI_SchoolYear", 
        @{ "SourceColumnName" = ""; }) | Out-Null
    
    ##$global:CUSTOM_COLUMN_MAP.Add("DI_AssessedGradeLevel", 
    ##    @{ "SourceColumnName" = "Grade"; }) | Out-Null
    
    $global:CUSTOM_COLUMN_MAP.Add("DI_AssessmentStudentIdentifier", 
        @{ "SourceColumnName" = "" }) | Out-Null

    $global:CUSTOM_COLUMN_MAP.Add("DI_PerfLevel_Descriptor", 
        @{ "SourceColumnName" = "Performance Level"; }) | Out-Null


    # Assesment specific functions

    # Get Normalized output column name given an source column name
    function Get-DIColumnName($srcName) {
        return "DI_{0}" -f $srcName
    }

    # Returns Hastable/lookup for current assessment being processed
    function Get-LevelLookup() {
        return $global:LEVEL_LOOKUP
    }

}

process {
    $currentLineNumber += 1
    $global:statistics.rowsProcessed += 1

    # Make sure we got something
    if (!$line) { Write-Error "Line $currentLineNumber does not contain any data"; return }

    # File is fixed width and does not contain headers, so on first line output headers before exporting calues
    # If the first line then output all of the headers
    if ($currentLineNumber -eq 1) { 

        # We assume headers will not have commas in the names
        #$headerRow = $line.Split(",")    
        $headerRow = Get-ParsedRecordArray -line $line

        $outputHeaders = @()

        # 1 - Loop thru headers to find and fix empty of duplicate headers
        for ($i = 0; $i -lt $headerRow.Count; $i++) {
            $item = $headerRow[$i]

            # handle empty headers
            if (!$item) {
                $newName = "EMPTY-$i"
                Write-Warning "Found empty column at position $($i + 1). Renaming column to '$newName'."
                $item = $newName
            }
            # handle duplicate headers
            if ($global:headersLookup.contains($item)) {
                $newName = "$item-$i"
                Write-Warning "Found duplicate column name of '$item' at position $($i + 1). Renaming column to '$newName'."
                $item = $newName
            }
            $global:inputHeaders += $item            
            $outputHeaders += $item
            $global:headersLookup.Add($item, $index) | Out-Null        
        }      

        # 2 - If there are custom headers add to output header line
        if ($global:CUSTOM_COLUMN_MAP.Count) {            
            $outputHeaders += $( $global:CUSTOM_COLUMN_MAP.Keys | Sort-Object )
        }


        Write-Output $(Get-CSVLine($outputHeaders))
        return
        #does not return after headers because first line contains values not headers, headers are fixed
    }

    # array, Ordered output values
    $orderedOutputValues = @()

    # HashTable, will hold all input values for current line, used in case computed columns need to reference input values
    $row = @{}
    
    # Not the cleanest of solutions, but none of the values should have double quotes, replace to remove wrapping quotes if present
    #$normalizedLine = $line.Replace('"', '')
    $row = ConvertFrom-Csv -InputObject $line -Header $global:inputHeaders
    #$row = Get-ParsedRecord -headers $($global:inputHeaders) -line $line

    # 1 - add all values as they come in line
    #$orderedOutputValues = $line.Split(",")
    $orderedOutputValues = Get-ParsedRecordArray -line $line

    # Validation will tell whether or not a record has the required values needed to be posted to ODS, 
    # if not valid error code and line number will be displayed in the summary
    $passedValidation = $true
    $validationId = $null
    # 2 - Process custom/computed columns
    if ($global:CUSTOM_COLUMN_MAP.Count) {            
        foreach ($outColumnName in  $( $global:CUSTOM_COLUMN_MAP.Keys | Sort-Object )) {
                    
            $srcInfo = $global:CUSTOM_COLUMN_MAP[$outColumnName]
            $srcColName = $srcInfo.SourceColumnName   
            
            ### DataImport Assessment Custom Columns
            switch -Exact ($outColumnName) {
                # StudentId
                "DI_StudentId" {
                    $rawStdId = $row.$srcColName
                    if ($rawStdId) {
                        $rawStdId = $rawStdId.Trim()
                    }
                    $stdId = Get-StudentODSId($rawStdId)

                    # Validation, this is a required field, if empty will be unable to post to ODS.
                    if (!$stdId) {
                        $passedValidation = $false
                        $validationId = "InvalidStudentId"
                        break
                    }
    
                    $orderedOutputValues += $stdId
                    break
                }
    
                # Admin Date
                "DI_AdministrationDate" {
                    
                    ## admin date , depends on SchoolYear which is extracted from filename
                    $adminDate = $null
                    if ($global:SCHOOL_YEAR) {
                        $adminDate = "{0}-04-15" -f $($global:SCHOOL_YEAR)   
                    }

                    # Validation, this is a required field, if empty will be unable to post to ODS
                    if (!$adminDate) {
                        $passedValidation = $false
                        $validationId = "InvalidAdministrationDate"
                        break
                    }
    
                    $orderedOutputValues += $adminDate
                    break
                }
    
                # School Year
                "DI_SchoolYear" {
                    # school year is extracted from filename
                    $schoolYear = $global:SCHOOL_YEAR

                    # Validation, this is a required field, if empty will be unable to post to ODS
                    if (!$schoolYear) {
                        $passedValidation = $false
                        $validationId = "InvalidSchoolYear"
                        break
                    }
    
                    $orderedOutputValues += $schoolYear
                    break
                }
    
                # Assessment Student Identifier
                "DI_AssessmentStudentIdentifier" {    
                    $assessStudentId = $global:SCHOOL_YEAR
                    
                    # Validation, this is a required field, if empty will be unable to post to ODS
                    if (!$assessStudentId) {
                        $passedValidation = $false
                        $validationId = "InvalidAssessmentStudentIdentifier"
                        break
                    }
    
                    $orderedOutputValues += $assessStudentId
                    break
                }
                # PerfLevel_Descriptor
                "DI_PerfLevel_Descriptor" {
                    $out_PerfLevel = $null
                    $col_PerfLevel = $row.$srcColName

                    # Validation, this is a required field, if empty will be unable to post to ODS
                    if (!$col_PerfLevel) {
                        $passedValidation = $false
                        $validationId = "InvalidPerfLevel"
                        break
                    }
                    $out_PerfLevel = $col_PerfLevel.ToLower()

                    $orderedOutputValues += $out_PerfLevel
                    break
                }
    

                Default {
                    # In case of no implementation for custom column add $null output to preserve output format and order
                    $orderedOutputValues += $null
                    break;
                }
            }
        }
    }     

    # If not valid add to statistics to notify user at the end of execution and skip record and do not post current for current row this screening period to ODS
    if (!$passedValidation) {
        # Only print once per row
        if (!$errorInCurrentRow) {
            Write-Warning "Invalid record see statistics summary for details.($currentLineNumber)"
        }            

        # Check if this validation error already added for current screening period
        if ($global:statistics.validations[$validationId]) {
            $global:statistics.validations[$validationId] += $currentLineNumber
        }
        else {
            $global:statistics.validations.Add($validationId, @($currentLineNumber)) | Out-Null
        }
        
        # Skip current line do not pass values to DataImport
        return
    }

    Write-Output $(Get-CSVLine($orderedOutputValues))
    $global:statistics.rowsLoaded ++    
}

end {

    Write-Information "SUMMARY: `
        Rows Processed: $($global:statistics.rowsProcessed); `
        Rows Loaded: $($global:statistics.rowsLoaded)"

    foreach ($validationId in $global:statistics.validations.Keys) {
        Write-Warning "Error reason: '$validationId'"                
        Write-Information "Line number(s):"
        $lineNumbers = $global:statistics.validations[$validationId]
        $errorThreshold = 50
        if ($lineNumbers.Count -gt $errorThreshold) {
            Write-Warning "There are $($lineNumbers.Count) lines with errors. Will only print the first $errorThreshold line numbers where errors ocurred."
            $lineNumbers = $lineNumbers[0..$($errorThreshold - 1)]
        }
        Write-Warning "$([string]::Join(",",$lineNumbers))"
    }

}
