    function Get-DateStrFromTELPASAdminDate($adminDateStr) {
        <#                       
                        ## Admin Date
                        ## Possible admin values 
                        #
                        #
                        #  less than 4 digits 
                        #  0321    -> March 2021
                        #
                        #  {mm}{yy}
                        #
                        ####
                    #>
        # In case of missing 0 for the month "321" -> "0321"
        $adminDate = $null
        if ($adminDateStr) {
            if ($adminDateStr.Length -eq 3) {
                $adminDateStr = "0{0}" -f $adminDateStr
            }
    
            if ($adminDateStr.Length -eq 4) {
                $month = $adminDateStr.Substring(0, 2)
                $year = $adminDateStr.Substring(2, 2)
            
                # no day is provided, default to 15 of the month/year provided
                $adminDate = "{0}/15/20{1}" -f $month, $year
            }
        }
        return $adminDate
    }

